// Handle form submission.
const paymentForm = document.getElementById('payment-form');
const paymentButton = document.getElementById('payment-button');


//
function moneiCreateToken(event){

  event.preventDefault();//prevent defualt commit behavior
  paymentButton.disabled = true;

  monei
    .createToken(cardInput)
    .then(function (result) {
      console.log("crateToken show result: ",result);
      if (result.error) {
        // Inform the user if there was an error.
        container.classList.add('is-invalid');
        errorText.innerText = result.error;
        paymentButton.disabled = false;

      } else {
        // Send the token to MONEI.
        moneiTokenHandler(result.token);
      }
    })
    .catch(function (error) {
      paymentButton.disabled = false;
      console.log(error);
    });
}


//paymentForm.removeEventListener("submit", moneiCreateToken);

paymentForm.addEventListener('submit', moneiCreateToken);



// Confirm the payment
function moneiTokenHandler(token) {
  return monei
    .confirmPayment({
      paymentId: '${functions.jsEncode(input.customFields.paymentId)}',
      paymentToken: token
    })
    .then(function (result) {

   console.log('moneiTokenHandler result is ',result);


    makePayment(token);

    })
    .catch(function (error) {
      console.log(error);
    });
}  

})(window.Opf,window.monei);


